import "./singlePost.css"
import { useNavigate } from "react-router-dom";

const SinglePost = ({ artist, city, title, description, image, postLike, _id, avatar }) => {

    const navigate = useNavigate();

    const navigateToDetails = () => {
        navigate("/author/" + _id)
    }

    return (
        <div data-testid="book-card">
            <div className="card-container-light my-3">
                <h2 className="card-title py-2 text-truncate">{title}</h2>
                <img className="card-img" src={image} alt={title} />
                <p>Author: {artist}</p>
                <p>Category: {city}</p>
                <p>Read Time: {description} min</p>
                <div className="d-flex justify-content-between">
                    <button className="btn btn-danger">Delete</button>
                    <button onClick={navigateToDetails} className="btn btn-secondary">Details</button>
                </div>
            </div>
        </div>
    )
}

export default SinglePost;
