import "./singlePost.css"
import { useNavigate } from "react-router-dom";

const SinglePost = ({ artist, city, title, description, image, postLike, _id, avatar }) => {

    const navigate = useNavigate();

    const navigateToDetails = () => {
        navigate("/author/" + _id)
    }

    return (
        <div className="post-card">
            <div className="d-flex align-items-center gap-2 post-artist-section">
                <div className="artist-picture">
                    <img className="rounded-circle" src={avatar} alt="" />
                </div>
                <div className="d-flex flex-column">
                    <p className="m-0" onClick={navigateToDetails}>{artist}</p>
                    <p className="m-0">{city}</p>
                </div>
            </div>
            <div>
                <img src={image} alt="post Image" />
            </div>
        </div>
    )
}

export default SinglePost;
