import "./singlePost.css"
import { useNavigate } from "react-router-dom";

const SinglePost = ({ artist, city, title, description, image, postLike, _id, avatar }) => {

    const navigate = useNavigate();

    const navigateToDetails = () => {
        navigate("/author/" + _id)
    }

    return (
        <div className="post-card" onClick={navigateToDetails}>
            <div className="d-flex post-artist-section">
                <div className="profile-picture">
                    <img className="rounded-circle" src={avatar} alt="" />
                </div>
                <h2 className="card-title py-2 text-truncate">{title}</h2>
                <img className="card-img" src={image} alt={title} />
                <p>Author: {artist}</p>
                <p>Category: {city}</p>
                <p>Read Time: {description} min</p>
            </div>
        </div>
    )
}

export default SinglePost;
